<?phpclass Vioactivate{	static function activation(){		global $wpdb;		$charset_collate = $wpdb->get_charset_collate();				//Twitter Social Table Code Start Here		$twitter_table 	= $wpdb->prefix . 'vio_twitter';		$twitter_sql 	= "CREATE TABLE IF NOT EXISTS $twitter_table (		`id` bigint(20) NOT NULL AUTO_INCREMENT,		`consumer_key` text COLLATE utf8_unicode_ci NOT NULL,		`consumer_secret` text COLLATE utf8_unicode_ci NOT NULL,		`access_token` text COLLATE utf8_unicode_ci NOT NULL,		`access_token_secret` text COLLATE utf8_unicode_ci NOT NULL,		`user_name` varchar(255) COLLATE utf8_unicode_ci NOT NULL,		`created` datetime NOT NULL,		`modified` datetime NOT NULL,		PRIMARY KEY (`id`)		) $charset_collate;";		//Twitter Social Table Code End Here		//Pinterest Social Table Code Start Here		$pinterest_table 	= $wpdb->prefix . 'vio_pinterest';		$pinterest_sql 		= "CREATE TABLE IF NOT EXISTS $pinterest_table (		`id` bigint(20) NOT NULL AUTO_INCREMENT,		`user_name` varchar(255) COLLATE utf8_unicode_ci NOT NULL,		`board_name` varchar(255) COLLATE utf8_unicode_ci NOT NULL,		`access_token` text COLLATE utf8_unicode_ci NOT NULL,		`created` datetime NOT NULL,		`modified` datetime NOT NULL,		PRIMARY KEY (`id`)		) $charset_collate;";		//Pinterest Social Table Code End Here		//Content Table Code Start Here		$content_table 	= $wpdb->prefix . 'vio_content';		$content_sql 	= "CREATE TABLE IF NOT EXISTS $content_table (		`id` bigint(20) NOT NULL AUTO_INCREMENT,		`title` varchar(255) COLLATE utf8_unicode_ci NOT NULL,		`description` longtext COLLATE utf8_unicode_ci NOT NULL,		`thumb` varchar(255) COLLATE utf8_unicode_ci NOT NULL,		`video_url` text COLLATE utf8_unicode_ci NOT NULL,		`post_id` bigint(20) NOT NULL,		`cat_id` text COLLATE utf8_unicode_ci NOT NULL,		`post_status` int(1) NOT NULL COMMENT '0: Draft, 1: Published',		`campaign_type` int(1) NOT NULL COMMENT '0: One Time',		`added_date` datetime NOT NULL,		`modified_date` datetime NOT NULL,		`published_date` datetime NOT NULL,		`c_lock` int(1) NOT NULL,		`post_author` bigint(20) NOT NULL,		PRIMARY KEY (`id`)		) $charset_collate;";		//Content Social Table Code End Here		//Social Content Table Code Start Here		$content_social_table 	= $wpdb->prefix . 'vio_content_social';		$content_social_sql 	= "CREATE TABLE IF NOT EXISTS $content_social_table (		`id` bigint(20) NOT NULL AUTO_INCREMENT,		`campaign_id` bigint(20) NOT NULL,		`post_id` bigint(20) NOT NULL,		`source` int(1) NOT NULL COMMENT '1: Twitter, 2: Pinterest',		`social_id` varchar(255) COLLATE utf8_unicode_ci NOT NULL,				`tw_consumer_key` text COLLATE utf8_unicode_ci NOT NULL,		`tw_consumer_secret` text COLLATE utf8_unicode_ci NOT NULL,		`tw_access_token` text COLLATE utf8_unicode_ci NOT NULL,		`tw_access_token_secret` text COLLATE utf8_unicode_ci NOT NULL,		`pi_access_token` text COLLATE utf8_unicode_ci NOT NULL,		`pi_user_name` varchar(255) COLLATE utf8_unicode_ci NOT NULL,		`pi_board_name` varchar(255) COLLATE utf8_unicode_ci NOT NULL,		`post_title` text COLLATE utf8_unicode_ci NOT NULL,		`post_image` text COLLATE utf8_unicode_ci NOT NULL,		`video_url` text COLLATE utf8_unicode_ci NOT NULL,		`post_url` text COLLATE utf8_unicode_ci NOT NULL,		`post_desc` text COLLATE utf8_unicode_ci NOT NULL,		`added_date` datetime NOT NULL,		`updated_date` datetime NOT NULL,		`posted_date` datetime NOT NULL,		`status` int(1) NOT NULL COMMENT '1: Posted, 0: Pending, 2: Error',		`c_lock` int(1) NOT NULL DEFAULT '0',		`post_response` text COLLATE utf8_unicode_ci NOT NULL,		PRIMARY KEY (`id`)		) $charset_collate;";		//Social Content Table Code End Here		require_once( ABSPATH . 'wp-admin/includes/upgrade.php' );		dbDelta( $twitter_sql );		dbDelta( $pinterest_sql );		dbDelta( $content_sql );		dbDelta( $content_social_sql );			}		static function deactivation(){	}	static function uninstall(){		global $wpdb;				$wpdb->query("DROP TABLE IF EXISTS {$wpdb->prefix}vio_twitter");		$wpdb->query("DROP TABLE IF EXISTS {$wpdb->prefix}vio_pinterest");		$wpdb->query("DROP TABLE IF EXISTS {$wpdb->prefix}vio_content");		$wpdb->query("DROP TABLE IF EXISTS {$wpdb->prefix}vio_content_social");	}}